package com.javaBasics.hexToRTF;

import org.apache.commons.codec.binary.Hex;

public class HexToRTF {

	public static void main(String[] args) {

		char[] hex = ""
				.toCharArray();
		try {
		byte[] array = Hex.decodeHex(hex);
		System.out.println(new String(array, "UTF-8"));
		}
		catch(Exception e) {
			e.printStackTrace();
		}
	}

}
